# you got a problem with that?

CUR="core/load_gl.h"
CURF="core/load_gl."

echo "

// AUTOGENERATED FILE

#ifndef FST_GL_LOADER_H
#define FST_GL_LOADER_H

#include <core/macros.h>

#ifdef __cplusplus
extern \"C\" {
#endif

#include <GL/gl.h>

#define FUNC(name, ret_type, ...) \\
    typedef ret_type (APIENTRYP name##_t)(__VA_ARGS__); \\
    extern name##_t name

extern b8 fst_use_wayland;

void fst_gl_load(void);  

" > "$CUR"

sed -E 's/^(.*\S.*)$/FUNC(\1);/' "$CURF" >> "$CUR"

echo "
#ifdef __cplusplus
}
#endif

#endif" >> "$CUR"


CUR="core/load_gl.c"

echo "

// AUTOGENERATED FILE

#include <stdio.h>
#include \"load_gl.h\"

#include <core/macros.h>

#ifdef _WIN32
    #include <windows.h>

    #define LOAD(name) \\
                do { \\
                    name = (name##_t)wglGetProcAddress(#name); \\
                    if (!name) { printf(\"failed to load %s!\n\", #name); return } \\
                } while (0)
#else
    #include <EGL/egl.h>
    #include <GL/glx.h>

    #define LOAD(name) \\
                do { \\
                    if (fst_use_wayland) name = (name##_t)eglGetProcAddress(#name); \\
                    else name = (name##_t)glXGetProcAddress((const GLubyte*)name); \\
                    if (!name) { printf(\"failed to load %s!\n\", #name); return; } \\
                } while(0)
#endif

#define FUNC_C(name, ret_type, ...) \\
    name##_t name = NULL

b8 fst_use_wayland;

" > "$CUR"

sed -E 's/^(.*\S.*)$/FUNC_C(\1);/' "$CURF" >> "$CUR"

echo "
void fst_gl_load(void) {
" >> "$CUR"

sed -E 's/^([^,]+),.*$/LOAD(\1);/' "$CURF" >> "$CUR"

echo "}" >> "$CUR"

